$(document).ready(function () {

	var mixer = mixitup('#portfolio_grid');

	var options = {
		offset: 200
	}

	var header = new Headhesive('header .nav', options);

	$(".portfolio li").click(function () {
		$(".portfolio li").removeClass("active");
		$(this).addClass("active");
	});

	$('.slider').slick({
		arrows: false,
		dots: true,
		infinite: true,
		slidesToShow: 1,
		slidesToScroll: 1,
		autoplay: true,
		autoplaySpeed: 10000,
		responsive: [
			{
				breakpoint: 1000,
				settings: {
					dots: false
				}
			}
		]
	});

	$('.review__slider').slick({
		arrows: true,
		dots: false,
		infinite: true,
		slidesToShow: 1,
		slidesToScroll: 1,
		autoplay: false,
		autoplaySpeed: 5000
	});

	$('.popup-with-form').magnificPopup({
		type: 'inline',
		midClick: true,
		mainClass: 'mfp-fade'
	});

	$(document).ready(function () {
		var link = $(".menu-link");
		var link_active = $("menu-link_active");
		var menu = $(".nav-burger");
		var nav_link = $(".nav-burger a");

		link.click(function () {
			link.toggleClass("menu-link_active");
			menu.toggleClass("nav-burger_active");
		});
		nav_link.click(function () {
			link.toggleClass("menu-link_active");
			menu.toggleClass("nav-burger_active");
		});
	})

	$('a[href="#review"]').click(function (t) {
		t.preventDefault();
		var e = $(".review");
		$("body,html").animate({
			scrollTop: $(e).offset().top - 150
		}, 1e3)
	}),
	$('a[href="#contact"]').click(function (t) {
		t.preventDefault();
		var e = $(".contact-form");
		$("body,html").animate({
			scrollTop: $(e).offset().top
		}, 1e3)
	})
})